const contactButtons=document.querySelectorAll(".js-contactButton"),contactContents=document.querySelectorAll(".js-contactContent"),contactWrapper=document.querySelector(".js-contactWrapper"),form=document.querySelector(".form");window.addEventListener("DOMContentLoaded",(()=>{contactButtons.forEach((t=>{t.addEventListener("click",(t=>{const e=t.target.dataset.name;contactButtons.forEach((e=>{e.classList.remove("is-show"),t.target.classList.add("is-show")})),contactContents.forEach((t=>{t.classList.remove("is-show"),t.dataset.name==e&&t.classList.add("is-show")}))}))}))})),form.addEventListener("submit",(function(t){t.preventDefault();const e=new FormData(form);fetch(form.action,{method:"POST",body:e,headers:{Accept:"application/json"}}).then((t=>{t.ok?(alert("Thank you for your message!"),form.reset()):alert("There was an error. Please try again!")}))}));